    background:none;
    border: 1px solid #777;
    border-radius: 3px;
    display:block;
    width:100%;
    box-sizing: border-box;
    padding: 10px;
    margin-bottom: 20px;
    color: black;
`;


const QuestionBodyTextarea = styled.textarea`
    background:none;
    border: 1px solid #777;
    border-radius: 3px;
    display:block;
    width:100%;
    box-sizing: border-box;
    padding: 10px;
    min-height: 200px;
    color: black;
    font-family: inherit;
`;

const AskTitle = styled.h1`
  font-size: 1.8rem;
  margin-bottom: 60px;
`;

const BlueButtonAsk = styled.button`
background-color:#0a95ff;
color:  white;
border: 0;
border-radius: 5px;
padding: 10px;
text-decoration: none;

`;

const PreviewArea = styled.div`
    padding: 20px;
    background-color: #ddd;
    border-radius: 5px;
    margin-top: 20px;
    margin-bottom: 20px;

`;




